mapper.delete:
update dcp_service_model
set status_code = 'deleted'
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select * from dcp_service_model t
where t.id = #{id,jdbcType=VARCHAR}
and status_code != 'deleted'

select * from dcp_service_model
status_code != 'deleted '
and model_name = #{modelName,jdbcType=VARCHAR}

select *
from dcp_service_model
where status_code != 'deleted' and topic_id = #{topicId,jdbcType=VARCHAR}

select *
from dcp_service_model
where status_code != 'deleted' and service_source_id = #{serviceSourceId,jdbcType=VARCHAR}

select * from dcp_service_model
parent_id is null and status_code != 'deleted'
and topic_id = #{topicId,jdbcType=VARCHAR}

select a.id as id, a.parent_id, a.model_name, a.model_desc, a.type_code, a.topic_id, a.service_source_id, a.status_code, a.request_method, a.expression_type, a.expression, a.cache_duration, a.body_pattern, a.create_time, a.create_by, a.update_time, a.update_by from dcp_service_model a left join dcp_data_assets_topic b on a.topic_id = b.id left join dcp_data_assets_area c on b.area_id = c.id left join dcp_service_source d on a.service_source_id = d.id
a.status_code != 'deleted'
and c.area_name like CONCAT('%',#{areaName,jdbcType=VARCHAR},'%')
and b.topic_name like CONCAT('%',#{topicName,jdbcType=VARCHAR},'%')
and d.service_source_name like CONCAT('%',#{serviceSourceName,jdbcType=VARCHAR},'%')
and a.model_name like CONCAT('%',#{modelName,jdbcType=VARCHAR},'%')
and a.type_code = #{typeCode,jdbcType=VARCHAR}
and a.status_code = #{statusCode,jdbcType=VARCHAR}
and a.parent_id = #{parentId,jdbcType=VARCHAR}
and b.owner =#{userId,jdbcType=BIGINT}
order by a.update_time desc

select a.id as id, a.parent_id, a.model_name, a.model_desc, a.type_code, a.topic_id, a.service_source_id, a.status_code, a.request_method, a.expression_type, a.expression, a.cache_duration, a.body_pattern, a.create_time, a.create_by, a.update_time, a.update_by from dcp_service_model a left join dcp_data_assets_topic b on a.topic_id = b.id left join dcp_data_assets_area c on b.area_id = c.id left join dcp_service_source d on a.service_source_id = d.id
a.status_code != 'deleted'
and c.area_name like CONCAT('%',#{areaName,jdbcType=VARCHAR},'%')
and b.topic_name like CONCAT('%',#{topicName,jdbcType=VARCHAR},'%')
and d.service_source_name like CONCAT('%',#{serviceSourceName,jdbcType=VARCHAR},'%')
and a.model_name like CONCAT('%',#{modelName,jdbcType=VARCHAR},'%')
and a.type_code = #{typeCode,jdbcType=VARCHAR}
and a.request_method = #{requestMethod,jdbcType=VARCHAR}
and a.status_code = #{statusCode,jdbcType=VARCHAR}
and b.owner =#{userId,jdbcType=BIGINT}
order by a.update_time desc

select a.id as id, a.parent_id, a.model_name, a.model_desc, a.type_code, a.topic_id, a.service_source_id, a.status_code, a.request_method, a.expression_type, a.expression, a.cache_duration, a.body_pattern, a.create_time, a.create_by, a.update_time, a.update_by from dcp_service_model a left join dcp_code b on a.topic_id = b.id left join dcp_code c on b.parent_code = c.code left join dcp_service_source d on a.service_source_id = d.id
a.status_code != 'deleted'
and c.display_name like CONCAT('%',#{areaName,jdbcType=VARCHAR},'%')
and b.display_name like CONCAT('%',#{topicName,jdbcType=VARCHAR},'%')
and d.service_source_name like CONCAT('%',#{serviceSourceName,jdbcType=VARCHAR},'%')
and a.model_name like CONCAT('%',#{modelName,jdbcType=VARCHAR},'%')
and a.type_code = #{typeCode,jdbcType=VARCHAR}
and a.request_method = #{requestMethod,jdbcType=VARCHAR}
and a.status_code = #{statusCode,jdbcType=VARCHAR}
and b.owner =#{userId,jdbcType=BIGINT}
order by a.update_time desc

