mapper.delete:
update dcp_app
set status_code = 'deleted'
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select * from dcp_app app
left join dcp_auth_user users on app.owner = users.id
where app.status_code != 'deleted' and app.owner = #{userId,jdbcType=VARCHAR}

