mapper.select:
select a.* from dgp_sys_audit_log a
left join dgp_auth_user b on a.user_id = b.id


        and b.login_name like concat('%',#{userName,jdbcType=VARCHAR},'%')


        and a.status = #{status,jdbcType=VARCHAR}


        and a.create_time >= #{startTime,jdbcType=VARCHAR}


        and a.create_time <= #{endTime,jdbcType=VARCHAR}


order by a.create_time desc

select count(1) from dgp_sys_audit_log a
left join dgp_auth_user b on a.user_id = b.id


        and b.login_name like concat('%',#{userName,jdbcType=VARCHAR},'%')


        and a.status = #{status,jdbcType=VARCHAR}


        and a.create_time >= #{startTime,jdbcType=VARCHAR}


        and a.create_time <= #{endTime,jdbcType=VARCHAR}
