mapper.select:
select

from dgp_sys_config


        and config_type = #{configType,jdbcType=VARCHAR}


        and config_subtype = #{configSubtype,jdbcType=VARCHAR}


order by create_time desc

mapper.insert:
insert into dgp_sys_config (
config_type
, config_subtype
, config_name
, default_value
, config_value
, display_name
, config_desc
, readonly
, create_time
, create_by
) values (
#{ configType ,jdbcType=VARCHAR}
, #{ configSubtype ,jdbcType=VARCHAR}
, #{ configName ,jdbcType=VARCHAR}
, #{ defaultValue ,jdbcType=VARCHAR}
, #{ configValue ,jdbcType=VARCHAR}
, #{ displayName ,jdbcType=VARCHAR}
, #{ configDesc ,jdbcType=VARCHAR}
, #{ readOnly ,jdbcType=VARCHAR}
,SYSDATE()
, #{ createBy,jdbcType=BIGINT}
)

mapper.update:
update dgp_sys_config
set
update_time = SYSDATE()
, update_by = #{ updateBy ,jdbcType=BIGINT}

    , config_type = #{configType,jdbcType=VARCHAR}


    , config_subtype = #{configSubtype,jdbcType=INTEGER}


    , config_name = #{configName,jdbcType=VARCHAR}


    , default_value = #{defaultValue,jdbcType=VARCHAR}


    , config_value = #{configValue,jdbcType=VARCHAR}


    , display_name = #{displayName,jdbcType=VARCHAR}


    , config_desc = #{ configDesc  ,jdbcType=VARCHAR}


    , readonly = #{ readOnly,jdbcType=VARCHAR }

where
id = #{id,jdbcType=BIGINT}
